INSERT INTO Event_Promoter_Association (id_event, id_promoter)
SELECT e.id,
	   IF(
			   RAND() < 0.5,
			   (SELECT id FROM Account WHERE email = 'promoter@test.com'),
			   (SELECT id FROM Account WHERE reference_type = 'PROMOTER' ORDER BY RAND() LIMIT 1)
	   )
FROM Event e
		 LEFT JOIN Event_Promoter_Association epa ON epa.id_event = e.id
WHERE epa.id IS NULL;

